'                for c2 in ( select page_id',
'                            from apex_application_page_buttons',
'                            where application_id = p_application_id',
'                                and button_id = p_param ) loop',
'                    if l_version < 5 then',
'                        l_app := 4000;',
'                        l_page := 4314;',
'                        l_link := '':::RP,4314:FB_FLOW_ID,FB_FLOW_PAGE_ID,F4000_P4314_ID:''',
