'                            then 5',
'                            else 1',
'                        end as height',
'                    from dual',
'            ) src',
'            on ( dest.flexible_table = src.table_name',
'                and dest.flexible_column = src.column_name )',
'            when not matched then',
'                insert (',
'                    dest.flexible_table,',
'                    dest.flexible_column,',
'                    dest.active_yn,',
'                    dest.form_label_text,',
'                    dest.is_required_yn,',
'                    --',
'                    dest.report_css_class,',
'                    dest.table_prefix,',
'                    dest.display_name_sing,',
'                    dest.display_name_plrl,',
'                    --',
'                    dest.label_template_id,',
'                    dest.display_as,',
'                    dest.width,',
'                    dest.max_width,',
'                    dest.height,',
'                    dest.is_displayed_on_ir,',
'                    dest.assigned_yn,',
'                    --',
'                    dest.condition_type,',
'                    dest.condition1,',
'                    dest.condition2',
'                )',
'                values (',
'                    src.table_name,',
'                    src.column_name,',
'                    ''N'',',
'                    src.column_name,',
'                    ''N'',',
'                    --',
