 p_id=>wwv_flow_api.id(1077389091960418469)
,p_plugin_id=>wwv_flow_api.id(8090826654681872463)
,p_name=>'SOURCE_SQL'
,p_is_required=>false
,p_sql_min_column_count=>2
,p_sql_max_column_count=>2
,p_examples=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>',
