'                                            p_n003 => ftr_rec.max,',
'                                            p_c015 => ftr_rec.inc_min,',
'                                            p_c016 => ftr_rec.inc_max,',
'                                            p_c010 => ''N'' );',
'            end loop;',
'        else',
'            -- Single number filter.',
'            apex_collection.add_member( p_collection_name => p_collection,',
'                                        p_c001 => p_column,',
'                                        p_c002 => p_header,',
'                                        p_c003 => p_display,',
'                                        p_c006 => ''NUMBER'',',
'                                        p_n001 => to_number(p_filter),',
'                                        p_c010 => ''N'' );',
'        end if;',
'    end add_numeric_filter;',
'',
